mapper.select:
SELECT * FROM (
SELECT rownum bnum, b.*
FROM (
SELECT * FROM board
WHERE
${searchItem} LIKE '%' || #{searchWord} || '%'
ORDER BY regdate DESC
) b
) WHERE bnum BETWEEN #{srow} and #{erow}

mapper.insert:
INSERT INTO board
(boardseq, usrid, title, content

    , originalfile, savedfile 

)
VALUES
(board_seq.nextval, #{usrid}, #{title}, #{content}

    , #{originalfile}, #{savedfile}


)

mapper.update:
UPDATE board
SET
    title = #{title}
    , content = #{content}
WHERE
    boardseq = #{boardseq}
